mapper.update:
UPDATE tb_brd_manage
SET	 brdType = #{brdType}
	,brdNm = #{brdNm}
	,brdPage = #{brdPage}
	,fileYn = #{fileYn}
	,notiYn = #{notiYn}
	,replyYn = #{replyYn}
	,cmtYn = #{cmtYn}
	,useYn = #{useYn}
	,delYn = #{delYn}
WHERE brdCd = #{brdCd}

UPDATE tb_intr_brd_noti
SET	 brdType = #{brdType}
	,brdTypeNm = #{brdTypeNm}
	,brdTl = #{brdTl}
	,brdCont = #{brdCont}
	,ancmOptnYn = #{ancmOptnYn}
WHERE brdCd = #{brdCd}

UPDATE tb_brd_faq
SET	 brdType = #{brdType}
	,brdTypeNm = #{brdTypeNm}
	,brdTl = #{brdTl}
	,brdCont = #{brdCont}
	,useYn = #{useYn}
WHERE brdCd = #{brdCd}

UPDATE tb_brd_gal
SET	 brdType = #{brdType}
	,brdTypeNm = #{brdTypeNm}
	,brdTl = #{brdTl}
	,brdCont = #{brdCont}
	,useYn = #{useYn}
	,img = #{img}
WHERE brdCd = #{brdCd}

mapper.select:
SELECT * FROM ${tblNm}
WHERE brdCd = (
	SELECT MIN(brdCd) FROM ${tblNm}
	WHERE brdCd  > 	 ${brdCd}
)

SELECT * FROM ${tblNm}
WHERE brdCd = (
	SELECT MAX(brdCd) FROM ${tblNm}
	WHERE brdCd  < 	 ${brdCd}
)
